import styled, { css } from 'styled-components';

const Button = styled.button`
  background-color: '#FFD82B'; 
  width: '220px';
  height: '47px';
  border: none;
  border-radius: 50px;
  font-family: "Montserrat";
  font-weight: 600;
  font-size: '16px';
  text-transform: uppercase;
  cursor: pointer;

  ${({secondary}) => (
      secondary && css`
      background-color: '#FFD82B';
      width: 105px;
      height: 30px;
      font-size: 10px;
      `
  )}
`;

export default Button;
